import React, { useState } from 'react';

function Iterator () {
	const [count, setCount] = useState(0);

	function add(){
		setCount(count + 1);
	}
	function red(){
		setCount(count - 1);
	}

	return (
	<div style={{margin: 0,auto}}>
		<div>
			<h1 style={{padding: 15}}>{count}</h1>
				<button onClick={add} style={{padding: 10 ,fontSize: 25}}>+</button>
				<button onClick={red} style={{padding: 10, fontSize: 25}}>-</button>
		</div>
	</div>
	);
}


export default Iterator;
