import React, { useState } from 'react';

function MyComponent() {
  const [isButtonTrue, setIsBackgroundTrue] = useState(false);

  const handleButtonClick = () => {
    setIsBackgroundTrue(!isButtonTrue);
  };

  const answer = isButtonTrue ? true : false;
  console.log(answer);
  // alert(answer);
  return (
    <div>
        <button onClick={handleButtonClick}>Change</button>
      </div>
  );
}

export default MyComponent;
