import React, { useState } from 'react';

function MyComponent() {
  const [isBackgroundRed, setIsBackgroundDark] = useState(false);

  const handleButtonClick = () => {
    setIsBackgroundDark(!isBackgroundRed);
  };

  const backgroundColor = isBackgroundRed ? 'black' : 'white';

  return (
    <div
      style={{
        backgroundColor: backgroundColor,
        padding: '20px',
        textAlign: 'center'
      }}
    >
      <button onClick={handleButtonClick}>Change Background Color</button>
    </div>
  );
}

export default MyComponent;
