import { useState } from 'react';

export default function App() {
  const [visible, setVisible] = useState(false);

  const handleToggle = () => {
    setVisible((current) => !current);
  };

  return (
    <div>
      <button onClick={handleToggle}>Show name</button>
      {visible && <p><img src="" alt="Sozder" /></p>}
    </div>
  );
}
