import React, { useState } from 'react';

function Iterator () {
	const [count, setCount] = useState(1);

	function kosu(){
		setCount(count + 1);
	}
	function azaitu(){
		setCount(count - 1);
	}
	function bolu(){
		setCount(count / 2);
	}
	function kobeitu(){
		setCount(count * 5);
	}

	return (
	<div>
		<div>
			<form action="#">
				<input type="number" name="number" id="" />
			</form>
			
				<h1 style={{padding: 15}}>{count}</h1>
				<button onClick={kosu} style={{padding: 10 ,fontSize: 25}}>+</button>
				<button onClick={azaitu} style={{padding: 10, fontSize: 25}}>-</button>
				<button onClick={bolu} style={{padding: 10 ,fontSize: 25}}>/</button>
				<button onClick={kobeitu} style={{padding: 10, fontSize: 25}}>*</button>
		</div>
	</div>
	);
}


export default Iterator;
