import React, { useState } from 'react';

function MyComponent() {
  const [isBackgroundRed, setIsBackgroundRed] = useState(false);

  const handleButtonClick = () => {
    setIsBackgroundRed(!isBackgroundRed);
  };
  const trueValue = 'true';
  const falseValue = 'false';
  const backgroundColor = isBackgroundRed ? 'true' : 'false';
  return (
    <div
      style={{
        backgroundColor: backgroundColor,width: 280,height: 320
      }}>
        <button onClick={handleButtonClick}>Change</button>
      </div>
  );
}

export default MyComponent;
